input = ""
potions = {'A': 0, 'B': 1, 'C': 3, 'D': 5}
total_potions = 0


for i in range(0, len(input), 3):
    if i + 2 >= len(input):
        break
    char1 = input[i]
    char2 = input[i + 1]
    char3 = input[i + 2]

    
    
    creature_count = 0
    if char1 != 'x':
        creature_count += 1
    if char2 != 'x':
        creature_count += 1
    if char3 != 'x':
        creature_count += 1
        
    if creature_count == 3:
        bonus = 2
    elif creature_count == 2:
        bonus = 1
    else:
        bonus = 0
        
    if char1 == 'x':
        value1 =0
    else:
        value1 = potions[char1] + bonus
    if char2 == 'x':
        value2 =0
    else:
        value2 = potions[char2] + bonus
    if char3 == 'x':
        value3 =0
    else:
        value3 = potions[char3] + bonus

    pair_potions = value1 + value2 + value3
    

    total_potions += pair_potions

    
print(total_potions)